import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = ({ isLoggedIn, setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    setIsLoggedIn(false);
    navigate("/login");
  };

  return (
    <nav className="bg-blue-600 p-4 text-white flex justify-between items-center">
      <h1 className="text-xl font-semibold">Flight Information Display System</h1>
      {isLoggedIn && (
        <div className="flex gap-4 items-center">
          {/* Navigation */}
          <Link to="/home" className="hover:underline">Home</Link>
          <Link to="/display" className="hover:underline">Display</Link>
          <Link to="/schedule" className="hover:underline">Schedule</Link>
          <Link to="/admin" className="hover:underline">Admin</Link>

          {/* Logout button */}
          <button
            onClick={handleLogout}
            className="ml-4 px-3 py-1 bg-white text-blue-600 rounded hover:bg-gray-100 font-medium"
          >
            Logout
          </button>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
