import React, { useState, useEffect } from "react";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import axios from "axios";
import "./Home.css"; // CSS file for styling

const Home = () => {
    const [flights, setFlights] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get("http://localhost:9999/api/flights") // Ensure this URL is correct
            .then((response) => {
                console.log("✅ API Response Data:", response.data); // Debugging log
                setFlights(response.data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("❌ Error fetching flight data:", error);
                setError("Failed to load data");
                setLoading(false);
            });
    }, []);

    const columnDefs = [
        { field: "sta", headerName: "STA", width: 90, cellClass: "center-text" },
        { field: "eta", headerName: "ETA", width: 90, cellClass: "center-text" },
        {
            field: "airline",
            headerName: "AIRLINE",
            width: 200,
            cellRenderer: (params) => (
                <div className="airline-cell">
                    <img src={params.data.logo} alt="Airline" className="airline-logo" />
                    {params.value}
                </div>
            ),
        },
        { field: "flight_number", headerName: "FLIGHT", width: 120, cellClass: "center-text bold-text" },
        { field: "origin", headerName: "ORIGIN", width: 180, cellClass: "center-text" },
        {
            field: "remark",
            headerName: "REMARK",
            width: 130,
            cellStyle: (params) => ({
                color: params.value === "LANDED" ? "green" : "red",
                fontWeight: "bold",
            }),
        },
    ];

    return (
        <div className="fids-container">
            <h2 className="header">INTERNATIONAL ARRIVAL</h2>

            {loading ? <p>Loading flights...</p> : null}
            {error ? <p className="error-message">{error}</p> : null}

            {/* Debugging: Show raw data */}
            <pre style={{ color: "white", background: "black", padding: "10px" }}>
                {JSON.stringify(flights, null, 2)}
            </pre>

            <div className="ag-theme-alpine fids-table">
                <AgGridReact
                    rowData={flights}
                    columnDefs={columnDefs}
                    domLayout="autoHeight"
                    rowHeight={50}
                    animateRows={true} // Helps ensure data updates
                />
            </div>
        </div>
    );
};

export default Home;
