import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import BackButton from "../components/BackButton";
import "./Signup.css";

const Signup = () => {
  const [form, setForm] = useState({
    userID: "",
    userName: "",
    password: "",
    airportCode: "",
    airlineCode: ""
  });

  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSignup = async (e) => {
    e.preventDefault();

    if (!form.userID || !form.userName || !form.password) {
      setError("User ID, Name, and Password are required.");
      return;
    }

    const hashPassword = async (password) => {
      const msgBuffer = new TextEncoder().encode(password);
      const hashBuffer = await crypto.subtle.digest("SHA-256", msgBuffer);
      return [...new Uint8Array(hashBuffer)].map(b => b.toString(16).padStart(2, "0")).join("");
    };

    const hashedPassword = await hashPassword(form.password);

    const newUser = {
      userID: form.userID,
      userName: form.userName,
      password: hashedPassword,
      airportCode: form.airportCode,
      airlineCode: form.airlineCode
    };

    try {
      const response = await fetch("http://localhost:9999/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser)
      });

      if (!response.ok) {
        const resText = await response.text();
        throw new Error(resText || "Signup failed.");
      }

      navigate("/login");
    } catch (err) {
      setError(err.message || "Signup failed.");
    }
  };

  return (
    <div className="signup-wrapper">
      <div className="signup-box">
        <h2 className="signup-title">Sign Up</h2>
        {error && <p className="signup-error">{error}</p>}

        <form onSubmit={handleSignup}>
          <div className="form-group">
            <label>User ID</label>
            <input name="userID" value={form.userID} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>User Name</label>
            <input name="userName" value={form.userName} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="password" name="password" value={form.password} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Airport Code</label>
            <input name="airportCode" value={form.airportCode} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Airline Code</label>
            <input name="airlineCode" value={form.airlineCode} onChange={handleChange} />
          </div>
          <button type="submit" className="signup-button">Create Account</button>
        </form>
        <BackButton label="← Back to Login" />


      </div>
    </div>
  );
};

export default Signup;
