import React, { useState, useEffect } from "react";
import { AgGridReact } from "ag-grid-react";
import { ClientSideRowModelModule } from "@ag-grid-community/client-side-row-model"; // ✅ Import required module
import { ModuleRegistry } from "@ag-grid-community/core"; // ✅ Register modules
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import axios from "axios";
import "./Home.css"; // Custom styling

// ✅ Register AG Grid modules
ModuleRegistry.registerModules([ClientSideRowModelModule]);

const Home = () => {
    const [flights, setFlights] = useState([]);
    const [gridApi, setGridApi] = useState(null);
    const [currentTime, setCurrentTime] = useState(new Date());

    useEffect(() => {
        axios.get("http://localhost:9999/api/flights")
            .then((response) => {
                console.log("✅ API Response Data:", response.data);

                const formattedData = response.data.map(flight => ({
                    id: flight.id,
                    sta: flight.sta || "--:--",
                    eta: flight.eta || "--:--",
                    airline: flight.airline || "Unknown",
                    logo: flight.logo || "https://via.placeholder.com/30", // Default logo
                    flight_number: flight.flight_number || "N/A",
                    origin: flight.origin || "Unknown",
                    remark: flight.remark || "Scheduled"
                }));

                setFlights(formattedData);

                if (gridApi) {
                    gridApi.setRowData(formattedData); // Ensure Grid Updates
                }
            })
            .catch(error => {
                console.error("❌ Error fetching flight data:", error);
            });

        const interval = setInterval(() => {
            setCurrentTime(new Date());
        }, 1000);

        return () => clearInterval(interval);
    }, [gridApi]);

    const columnDefs = [
        { field: "sta", headerName: "STA", width: 80, cellClass: "center-text bold-text" },
        { field: "eta", headerName: "ETA", width: 80, cellClass: "center-text bold-text" },
        {
            field: "airline",
            headerName: "AIRLINE",
            width: 220,
            cellRenderer: (params) => (
                <div className="airline-cell">
                    <img src={params.data.logo} alt="Airline Logo" className="airline-logo" />
                    <span className="airline-name">{params.value}</span>
                </div>
            ),
        },
        { field: "flight_number", headerName: "FLIGHT", width: 120, cellClass: "center-text bold-text" },
        { field: "origin", headerName: "ORIGIN", width: 180, cellClass: "center-text" },
        {
            field: "remark",
            headerName: "REMARK",
            width: 150,
            cellStyle: (params) => ({
                color: params.value === "LANDED" ? "limegreen" : params.value === "DELAYED" ? "red" : "white",
                fontWeight: "bold",
                textAlign: "center",
            }),
        },
    ];

    return (
        <div className="fids-container">
            <div className="fids-header">
                <h2 className="fids-title">INTERNATIONAL ARRIVAL</h2>
                <div className="fids-clock">{currentTime.toLocaleTimeString()}</div>
            </div>

            <div className="ag-theme-alpine-dark fids-table">
                <AgGridReact
                    rowData={flights}
                    columnDefs={columnDefs}
                    domLayout="autoHeight"
                    rowHeight={50}
                    headerHeight={50}
                    animateRows={true}
                    onGridReady={(params) => setGridApi(params.api)}
                />
            </div>
        </div>
    );
};

export default Home;
