import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = ({ setIsLoggedIn }) => {
  const [form, setForm] = useState({ identifier: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleLogin = (e) => {
    e.preventDefault();

    // Example: Valid user list (can be replaced with backend or JSON)
    const validUsers = [
      { id: "soon123", email: "test@example.com", password: "1234" },
      { id: "admin01", email: "admin@example.com", password: "admin" },
    ];

    // Match by ID or Email
    const matchedUser = validUsers.find(
      (user) =>
        (user.email === form.identifier || user.id === form.identifier) &&
        user.password === form.password
    );

    if (matchedUser) {
      setIsLoggedIn(true);
      navigate("/home");
    } else {
      setError("Invalid ID/email or password");
    }
  };

  return (
    <div>
      <h1 className="project-title">ADT Final Project (SoonYoeng)</h1>
      <div className="login-wrapper">
        <div className="login-box">
          <h2 className="login-title">Login</h2>
          {error && <p className="login-error">{error}</p>}

          <form onSubmit={handleLogin}>
            <div className="form-group">
              <label>ID or Email</label>
              <input
                name="identifier"
                value={form.identifier}
                onChange={handleChange}
                placeholder="Enter your ID or Email"
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                name="password"
                type="password"
                value={form.password}
                onChange={handleChange}
                placeholder="Enter your password"
              />
            </div>
            <button className="login-button" type="submit">
              Login
            </button>
          </form>

          <p className="signup-text">
            Donâ€™t have an account?{" "}
            <a href="/signup" className="signup-link">Sign up</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
