import React, { useState } from "react";
import axios from "axios";

const Admin = () => {
    const [newFlight, setNewFlight] = useState({
        flight_number: "",
        departure: "",
        arrival: "",
        status: "On Time",
    });

    const addFlight = () => {
        axios.post("http://localhost:5000/api/flights", newFlight).then(() => {
            alert("Flight added!");
        });
    };

    return (
        <div className="p-4">
            <h2 className="text-xl mb-4">Admin Panel</h2>
            <input
                placeholder="Flight Number"
                onChange={(e) => setNewFlight({ ...newFlight, flight_number: e.target.value })}
            />
            <input
                placeholder="Departure"
                onChange={(e) => setNewFlight({ ...newFlight, departure: e.target.value })}
            />
            <input
                placeholder="Arrival"
                onChange={(e) => setNewFlight({ ...newFlight, arrival: e.target.value })}
            />
            <select onChange={(e) => setNewFlight({ ...newFlight, status: e.target.value })}>
                <option value="On Time">On Time</option>
                <option value="Delayed">Delayed</option>
            </select>
            <button onClick={addFlight}>Add Flight</button>
        </div>
    );
};

export default Admin;
